import React, { useState, useCallback } from "react";

const useHttp = ({requestObj, transformData}) => {
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);

    const sendHttpRequest = useCallback( async (taskText) => {
        setIsLoading(true);
        setError(null);
        try {
          const response = await fetch(
            requestObj.url, {
                method: requestObj.method || 'GET',
                headers: requestObj.headers || null,
                body: requestObj.body ? JSON.stringify(requestObj.body) : null,
            }
          );

          if (!response.ok) {
            throw new Error('Request failed!');
          }

          const data = await response.json();
          const loadedTasks = transformData(data);

          setTasks(loadedTasks);
        } catch (err) {
          setError(err.message || 'Something went wrong!');
        }
        setIsLoading(false);
      }, []);

      return {
          isLoading,
          error,
          sendHttpRequest,
      }
};

export default useHttp;
