import React, { useReducer } from "react";
import CartContext from "./cart-context";

const defaultCartItem = {
    item: [],
    totalAmount: 0,
    addItem: () => {},
    removeItem: () => {},
};

const cartReducer = (state, action) => {
    if(action.type === 'ADD') {
        const updatedItem = state.item.concat(action.item);
        const updatedAmount = state.totalAmount + (parseFloat(action.item.price) * parseFloat(action.item.amount));

        return {
            item: updatedItem,
            totalAmount: updatedAmount,
        }
    }
    return defaultCartItem;
};

const CartContextProvider = (props) => {
    const [cartState, dispatchCartState] = useReducer(defaultCartItem, cartReducer);

    const onAddItem = (item) => {
        console.log('IN_CONTEXT', item);
        dispatchCartState({type: 'ADD', item:item});
    }

    const onRemoveItem = (id) => {
        dispatchCartState({type: 'REMOVE', id:id});
    }

    const cartItems = {
        item: cartState.item,
        totalAmount: cartState.totalAmount,
        addItem: onAddItem,
        removeItem: onRemoveItem,
    };

    return (
        <CartContext.Provider value={cartItems}>
            {props.children}
        </CartContext.Provider>
    )
};

export default CartContextProvider;
