import classes from './Modal.module.css';
import ReactDOM from 'react-dom';
import { useContext } from 'react';
import AuthContext from '../../../store/auth-context';
import * as S from './Modal.style';

const Modal = (props) => {
    const ctx = useContext(AuthContext);

    const classBackDrop = props.isSpinner ? classes.backdrop : classes.backdropSpinner;
    const classModal = props.isSpinner ? 'modal spinner' : 'modal';
    return ReactDOM.createPortal(
        <S.Wrapper>
        <S.BackDrop isSpinner={props.isSpinner} onClick={ctx.closeModal}/>
        <S.Modal isSpinner={props.isSpinner} data-locator='modal'>
            {props.children}
        </S.Modal>
        </S.Wrapper>,
        document.getElementById('portal')
    )
};

export default Modal;
