import classes from './MealsItem.module.css';

const MealsItem = (props) => {
    const {
        name,
        description,
        price,
    } = props;

    const priceInCurr = `$${price}`;

    return (
        <li>
            <div className={classes.meal}>
                <h3>{name}</h3>
                <h2>{description}</h2>
                <h2>{priceInCurr}</h2>
            </div>
        </li>
    );
};

export default MealsItem;
