import styled from "styled-components";


export const Button = styled.button`
    margin: 1rem;
    width: 15rem;
    border-radius: 10px;
`;

export const Items = styled.span``;

export const Wrapper = styled.div`
    display: flex;
    justify-content: space-evenly;
    align-items: center;
`;

export const Image = styled.img`
    width: 30px;
`;
