import React, { useState } from 'react';
import useInput from '../hooks/use-input';

const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState('');
  const [email, setEmail] = useState('');
  const [isEmailTouched, setIsEmailTouched] = useState(false);
  const [isNametouched, setIsNameTouched] = useState(false);

  const invalid = (enteredName.trim() === '' && isNametouched) || (isEmailTouched && (email.trim() === '' || !email.includes('@')));
  const invalidName = enteredName.trim() === '' && isNametouched;
  const invalidEmail = isEmailTouched && (email.trim() === '' || !email.includes('@'));

  const changeHandler = (e) => {
    setEnteredName(e.target.value);
  }

  const submitHandler = e => {
    e.preventDefault();
    setIsNameTouched(true);
    setIsEmailTouched(true);
    if(invalid) {
      return;
    }
    console.log(enteredName, email);
    setIsNameTouched(false);
    setEnteredName('');
    setIsEmailTouched(false);
    setEmail('');
  }

  const focusHandler = e => {
    setIsNameTouched(true);
  }

  const emailFocusHandler = e => {
    setIsEmailTouched(true);
  }

  const emailChangeHandler = e => {
    setEmail(e.target.value);
  }

  const className = invalidName ? 'form-control invalid' : 'form-control';
  const classEmail = invalidEmail ? 'form-control invalid' : 'form-control';
  return (
    <form onSubmit={submitHandler}>
      <div className={className}>
        <label htmlFor='name'>Your Name</label>
        <input
        type='text'
        id='name'
        onChange={changeHandler}
        value={enteredName}
        onBlur={focusHandler}
        />
      </div>
      {invalidName && (<p className='error-text'>Name is invalid.</p>)}
      <div className={classEmail}>
        <label htmlFor='name'>Email Address</label>
        <input
        type='email'
        id='email'
        onChange={emailChangeHandler}
        value={email}
        onBlur={emailFocusHandler}
        />
      </div>
      {invalidEmail && (<p className='error-text'>Email is invalid.</p>)}
      <div className="form-actions">
        <button disabled={invalid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
