import styled from 'styled-components';

export const Card = styled.div`
    width: 50%;
    border: 1px solid;
    border-radius: 10px;
    box-shadow: 0 1px 8px rgba(0, 0, 0, 0.25);
    margin: 2rem auto;
    padding: 1rem;
`;
